import {Component, OnInit, ViewEncapsulation} from '@angular/core';
import {ActivatedRoute} from '@angular/router';
import { NgModel } from '@angular/forms'; // Añade esta importación
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  encapsulation: ViewEncapsulation.None
  providers: [NgModel] // Agrega NgModel aquí
})
export class HomeComponent implements OnInit {

  constructor(
    private route: ActivatedRoute) {
  }
  isUserLoggedIn: boolean = false; // Declaración y asignación inicial

  needsLogin: boolean | undefined;
  _userName: string = '';

  ngOnInit() {
    this.needsLogin = !!this.route.snapshot.params['needsLogin'];
  }

  get userName(): string {
    return this._userName;
  }

  login(): void {
    this._userName = 'Max';
  }

  logout(): void {
    this._userName = 'Mix';
  }


}
<div class="card">
  <div class="header">
    <h1 class="title">Bienvenido {{userName}}</h1>
  </div>
  <div class="content">

    <div *ngIf="needsLogin" class="alert alert-warning">
      Ha sido redirigido a esta página porque no tiene la información necesaria
      derechos para la acción requerida. ¡Inicie sesión con una cuenta de usuario respectiva!
    </div>

    <button (click)="login()" class="btn btn-default">Iniciar Seccion</button>
    <button (click)="logout()" class="btn btn-default">Cerrar Seccion</button>
    <h1>Bienvenido a la Página de Inicio</h1>

    <h1>Bienvenido a la Página de Inicio</h1>

    <div *ngIf="!isUserLoggedIn">
      <form (ngSubmit)="login()">
        <div class="form-group">
          <label for="username">Nombre de Usuario:</label>
          <input type="text" id="username" name="username" [(ngModel)]="username" class="form-control" required>
        </div>

        <div class="form-group">
          <label for="password">Contraseña:</label>
          <input type="password" id="password" name="password" [(ngModel)]="password" class="form-control" required>
        </div>
        

        <button type="submit" class="btn btn-primary">Iniciar Sesión</button>
      </form>
    </div>

    <p *ngIf="isUserLoggedIn">Usuario: {{ userName }}</p>
    <button *ngIf="isUserLoggedIn" (click)="logout()" class="btn btn-danger">Cerrar Sesión</button>


  <!--- <tabbed-pane [activeId]="0">
        <table title="Booked" [id]="0">
          Esta página podría mostrar todos los pasajeros reservados ...
        </table>
        <table title="Checked in" [id]="1">
          Esta página podría mostrar todos los pasajeros facturados ...
        </table>

        <table title="Boarded" [id]="2">
          Esta página podría mostrar todos los pasajeros embarcados ...
        </table>
      </tabbed-pane> -->



  </div>

</div>
