import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  userName: string = ''; // Debes establecer el nombre de usuario
  needsLogin: boolean = false;
  isUserLoggedIn: boolean = false;

  ngOnInit() {
    // La lógica para verificar el estado de inicio de sesión va aquí
    // Puedes usar this.isUserLoggedIn para reflejar el estado actual
  }

  login(): void {
    // Aquí debes implementar la lógica de inicio de sesión.
    // Por ejemplo, verificar las credenciales y establecer isUserLoggedIn en true si el inicio de sesión es exitoso.
    this.isUserLoggedIn = true;
    this.userName = 'correpto'
  }

  logout(): void {
    // Aquí debes implementar la lógica de cierre de sesión.
    // Por ejemplo, establecer isUserLoggedIn en false y borrar el nombre de usuario.
    this.isUserLoggedIn = false;
    this.userName = 'incorrepto';
  }
}
<div class="card">
  <div class="header">
    <h1 class="title">Bienvenido {{userName}}</h1>
  </div>
  <div class="content">
    <div *ngIf="needsLogin" class="alert alert-warning">
      Ha sido redirigido a esta página porque no tiene la información necesaria
      derechos para la acción requerida. ¡Inicie sesión con una cuenta de usuario respectiva!
    </div>

    <button *ngIf="!isUserLoggedIn" (click)="login()" class="btn btn-default">Iniciar Sesión</button>

    <!-- Formulario de inicio de sesión -->
    <div *ngIf="!isUserLoggedIn">
      <form (ngSubmit)="login()">
        <div class="form-group">
          <label for="username">Nombre de Usuario:</label>
          <input type="text" id="username" name="username" [(ngModel)]="userName" class="form-control" required>
        </div>
        <div class="form-group">
          <label for="password">Contraseña:</label>
          <input type="password" id="password" name="password" class="form-control" required>
        </div>
        <button type="submit" class="btn btn-primary">Iniciar Sesión</button>
      </form>
    </div>

    <button *ngIf="isUserLoggedIn" (click)="logout()" class="btn btn-default">Cerrar Sesión</button>
  </div>
</div>



