import {Component, OnInit, ViewEncapsulation} from '@angular/core';
import {ActivatedRoute} from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  encapsulation: ViewEncapsulation.None

})
export class HomeComponent implements OnInit {

  constructor(
    private route: ActivatedRoute) {
  }
  isUserLoggedIn: boolean = false; // Declaración y asignación inicial

  needsLogin: boolean | undefined;
  _userName: string = '';

  ngOnInit() {
    this.needsLogin = !!this.route.snapshot.params['needsLogin'];
  }

  get userName(): string {
    return this._userName;
  }

  login(): void {
    this._userName = 'Max';
  }

  logout(): void {
    this._userName = 'Mix';
  }


}
<div class="card">
  <div class="header">
    <h1 class="title">Bienvenido {{userName}}</h1>
  </div>
  <div class="content">

    <div *ngIf="needsLogin" class="alert alert-warning">
      Ha sido redirigido a esta página porque no tiene la información necesaria
      derechos para la acción requerida. ¡Inicie sesión con una cuenta de usuario respectiva!
    </div>

    <button (click)="login()" *ngIf="!userName">Iniciar Sesión</button>
    <button (click)="logout()" *ngIf="userName">Cerrar Sesión</button>
<h1>Bienvenido a la Página de Inicio</h1>

<p *ngIf="!userName">No has iniciado sesión.</p>
<p *ngIf="userName">Usuario: {{ userName }}</p>

<button (click)="login()" *ngIf="!userName">Iniciar Sesión</button>
<button (click)="logout()" *ngIf="userName">Cerrar Sesión</button>

