import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  userName: string = ''; // Debes establecer el nombre de usuario
  needsLogin: boolean = false;
  isUserLoggedIn: boolean = false;

  ngOnInit() {
    // La lógica para verificar el estado de inicio de sesión va aquí
    // Puedes usar this.isUserLoggedIn para reflejar el estado actual
  }

  login(): void {
    // Aquí debes implementar la lógica de inicio de sesión.
    // Por ejemplo, verificar las credenciales y establecer isUserLoggedIn en true si el inicio de sesión es exitoso.
    this.isUserLoggedIn = true;
    this.userName = 'correpto'
  }

  logout(): void {
    // Aquí debes implementar la lógica de cierre de sesión.
    // Por ejemplo, establecer isUserLoggedIn en false y borrar el nombre de usuario.
    this.isUserLoggedIn = false;
    this.userName = 'incorrepto';
  }
}
