import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router'; // Importa el enrutador

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  userName: string = ''; // Debes establecer el nombre de usuario
  needsLogin: boolean = false;
  isUserLoggedIn: boolean = false;

  constructor(private router: Router) {} // Inyecta el enrutador en el constructor

  ngOnInit() {
    // La lógica para verificar el estado de inicio de sesión va aquí
    // Puedes usar this.isUserLoggedIn para reflejar el estado actual
  }

  login(susan: string, susand12: string): void {
    // Lógica de autenticación
    if (susan  === 'susan' && susand12 === 'susand12') {
      this.isUserLoggedIn = true;
      this.userName = 'correcto';

      // Redirige al usuario a la página de "Vuelos"
      this.router.navigate(['/flights']); // Asegúrate de que la ruta '/flights' coincida con la ruta real de tu página de "Vuelos" en las rutas de enrutamiento.
    }
  }

}
<div class="card">
  <div class="header">
    <h1 class="title">Bienvenido {{userName}}</h1>
  </div>
  <div class="content">
    <div *ngIf="needsLogin" class="alert alert-warning">
      Ha sido redirigido a esta página porque no tiene la información necesaria
      derechos para la acción requerida. ¡Inicie sesión con una cuenta de usuario respectiva!
    </div>

  <!-- Formulario de inicio de sesión -->
< *ngIf="!isUserLoggedIn">
  <form (ngSubmit)="login(susan.value, susand12.value)">
    <div class="form-group">
      <label for="username">Nombre de Usuario:</label>
      <input type="text" id="username" name="username" class="form-control" required #susan>
    </div>
    <div class="form-group">
      <label for="password">Contraseña:</label>
      <input type="password" id="password" name="password" class="form-control" required #susand12>
    </div>
    <button type="submit" class="btn btn-primary">Ingresar Sesión</button>
  </form>





<tabbed-pane [activeId]="0">
        <tab title="Booked" [id]="0">
          Esta página podría mostrar todos los pasajeros reservados ...
        </tab>
        <table title="Checked in" [id]="1">
          Esta página podría mostrar todos los pasajeros facturados ...
        </table>

        <table title="Boarded" [id]="2">
          Esta página podría mostrar todos los pasajeros embarcados ...
        </table>
      </tabbed-pane>
    </div>
